    // hard core copying the data where as we will use the apis
    const notesInitial = [
            {
              "_id": "64e853c61e2568853bb855f3",
              "user": "64e772939df48ad8e716418d",
              "title": "Rajdhani Tejas Express",
              "description": "IRCTC",
              "tag": "Trains 2.0",
              "date": "2023-08-25T07:09:58.936Z",
              "__v": 0
            },
            {
              "_id": "64e867468d296e13359da293",
              "user": "64e772939df48ad8e716418d",
              "title": "Duronto Express",
              "description": "Indian Railways",
              "tag": "Trains",
              "date": "2023-08-25T08:33:10.879Z",
              "__v": 0
            }
    ]
    const [notes, setNotes] = useState(notesInitial)

    // creating fuctions 

    // Add a Note
    const addNote = (title,description, tag) =>{
      // todo : api calls
      const note = {
        "_id": "61322f119553781a8ca8d0e08",
          "user": "6131dc5e3e4037cd4734a0664",
          "title": title,
          "description": description,
          "tag": tag,
          "date": "2021-09-03T14:20:09.668Z",
          "__v": 0
      };
      setNotes(notes.concat(note))
    }

    return(
        // <noteContext.Provider value={{state:state, update:update}}>
        //     {props.children}
        // </noteContext.Provider>
        <noteContext.Provider value = {{notes, setNotes}}>
            {props.children}
        </noteContext.Provider>
    )
}

export default NoteState;
